/**
 * God-Tier Image Configuration
 * Centralized image sources with blur placeholders and responsive hints
 *
 * Following Apple Design Guidelines for image optimization:
 * - Blur placeholders for instant perceived load
 * - Priority flags for above-the-fold images
 * - Responsive size hints for optimal loading
 * - Descriptive alt text for accessibility
 */

export interface HeroImage {
  src: string
  alt: string
  blurDataURL: string
  priority?: boolean // Preload above-the-fold images
  sizes?: string // Responsive sizes hint for Next.js
}

