/**
 * Helper to get optimized image URL for specific viewport
 * Used for advanced art direction (different crops per device)
 */
export function getOptimizedImageUrl(
  baseImage: string,
  viewport: 'mobile' | 'tablet' | 'desktop' = 'desktop',
  format: 'avif' | 'webp' | 'jpg' = 'avif'
): string {
  const base = baseImage.replace(/\.[^.]+$/, '')
  return `/${base}-${viewport}.${format}`
}

/**
 * Type guard to check if a string is a valid hero image key
 */
export function isHeroImageKey(key: string): key is keyof typeof HERO_IMAGES {
  return key in HERO_IMAGES
}
